import React, { useState } from "react";

const AddCompetitorForm = ({ eventId, addCompetitorsToHeat }) => {
  const [competitors, setCompetitors] = useState([
    { name: "", category: "", sub_category: "", board_type: "", gender: "", age_category: "" },
    { name: "", category: "", sub_category: "", board_type: "", gender: "", age_category: "" },
    { name: "", category: "", sub_category: "", board_type: "", gender: "", age_category: "" },
    { name: "", category: "", sub_category: "", board_type: "", gender: "", age_category: "" }
  ]);

  const handleChange = (index, field, value) => {
    const updatedCompetitors = [...competitors];
    updatedCompetitors[index][field] = value;
    setCompetitors(updatedCompetitors);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    addCompetitorsToHeat(competitors);
  };

  return (
    <form onSubmit={handleSubmit}>
      <h4>Add Competitors to Heat 1</h4>
      {competitors.map((competitor, index) => (
        <div key={index} className="mb-3">
          <h5>Competitor {index + 1}</h5>
          <div className="form-group">
            <label>Name:</label>
            <input
              type="text"
              className="form-control"
              value={competitor.name}
              onChange={(e) => handleChange(index, "name", e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label>Category:</label>
            <input
              type="text"
              className="form-control"
              value={competitor.category}
              onChange={(e) => handleChange(index, "category", e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label>Sub Category:</label>
            <input
              type="text"
              className="form-control"
              value={competitor.sub_category}
              onChange={(e) => handleChange(index, "sub_category", e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label>Board Type:</label>
            <input
              type="text"
              className="form-control"
              value={competitor.board_type}
              onChange={(e) => handleChange(index, "board_type", e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label>Gender:</label>
            <input
              type="text"
              className="form-control"
              value={competitor.gender}
              onChange={(e) => handleChange(index, "gender", e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label>Age Category:</label>
            <input
              type="text"
              className="form-control"
              value={competitor.age_category}
              onChange={(e) => handleChange(index, "age_category", e.target.value)}
              required
            />
          </div>
        </div>
      ))}
      <button type="submit" className="btn btn-primary">
        Add Competitors
      </button>
    </form>
  );
};

export default AddCompetitorForm;
