import React, { useState } from "react";

const AddCompetitorForm = ({ addCompetitorToHeat }) => {
  const [competitorName, setCompetitorName] = useState("");

  const handleAddCompetitor = (e) => {
    e.preventDefault();
    if (competitorName.trim() === "") {
      alert("Competitor name cannot be empty");
      return;
    }

    const newCompetitor = {
      id: Math.random().toString(36).substr(2, 9), // Generate a random id
      name: competitorName,
    };

    addCompetitorToHeat(newCompetitor);
    setCompetitorName("");
  };

  return (
    <form onSubmit={handleAddCompetitor} className="mb-4">
      <div className="form-group">
        <label>Competitor Name:</label>
        <input
          type="text"
          className="form-control"
          value={competitorName}
          onChange={(e) => setCompetitorName(e.target.value)}
          required
        />
      </div>
      <button type="submit" className="btn btn-primary mt-3">
        Add Competitor
      </button>
    </form>
  );
};

export default AddCompetitorForm;
