import React, { useState, useEffect } from "react";
import axios from "axios";
import { Container, Row, Col, Card, Button } from "react-bootstrap";
import EventsList from "./EventList";
import AddCompetitorForm from "./AddCompetitorForm";
import CompetitorsList from "./CompetitorList";
import JudgesList from "./JudgesList";
import AddJudgeForm from "./AddJudgeForm";
import GeneratePDFButton from "./GeneratePDFButton";
import RoundsDisplay from "./RoundsDisplay";

const categories = ["Shortboard", "Longboard"];
const subCategories = ["Men", "Women"];
const ageCategories = [
  "Open",
  "Sub12",
  "Sub14",
  "Sub18",
  "Professional",
  "+40",
  "+45",
  "+50",
  "+60",
  "+65",
  "+70",
];

const EventManagement = () => {
  const [events, setEvents] = useState([]);
  const [judges, setJudges] = useState([]);
  const [rounds, setRounds] = useState([]);
  const [eventId, setEventId] = useState(null);
  const [name, setName] = useState("");
  const [date, setDate] = useState("");
  const [location, setLocation] = useState("");
  const [judgeName, setJudgeName] = useState("");
  const [createdBy, setCreatedBy] = useState(1);
  const [selectedEventName, setSelectedEventName] = useState("");
  const [showCreateRoundButton, setShowCreateRoundButton] = useState(false);

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    try {
      const response = await axios.get(
        "http://localhost:3000/event-admin/get-events"
      );
      setEvents(response.data);
    } catch (error) {
      console.error("Error fetching events:", error);
    }
  };

  const fetchJudges = async (eventId) => {
    try {
      const response = await axios.get(
        `http://localhost:3000/event-admin/get-judges/${eventId}`
      );
      setJudges(response.data);
    } catch (error) {
      console.error("Error fetching judges:", error);
    }
  };

  const handleCreateEvent = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:3000/event-admin/create-event", {
        name,
        date,
        location,
        created_by: createdBy,
      });
      alert("Event created successfully");
      setName("");
      setDate("");
      setLocation("");
      fetchEvents();
    } catch (error) {
      console.error("Error creating event:", error);
      alert("Error creating event");
    }
  };

  const handleDeleteEvent = async (id) => {
    try {
      await axios.delete(
        `http://localhost:3000/event-admin/delete-event/${id}`
      );
      alert("Event deleted successfully");
      fetchEvents();
    } catch (error) {
      console.error("Error deleting event:", error);
      alert("Error deleting event");
    }
  };

  const handleDeleteJudge = async (id) => {
    try {
      await axios.delete(
        `http://localhost:3000/event-admin/delete-judge/${id}`
      );
      alert("Judge deleted successfully");
      fetchJudges(eventId);
    } catch (error) {
      console.error("Error deleting judge:", error);
      alert("Error deleting judge");
    }
  };

  const handleSaveRounds = async () => {
    try {
      await axios.post("http://localhost:3000/event-admin/save-rounds", {
        eventId,
        rounds: rounds.map((round) => ({
          name: round.name,
          category: round.category,
          sub_category: round.sub_category,
          board_type: round.board_type,
          gender: round.gender,
          age_category: round.age_category,
          heats: round.heats.map((heat) => ({
            heat_name: heat.name,
            competitors: heat.competitors.map((competitor) => ({
              id: competitor.id,
              name: competitor.name,
            })),
          })),
        })),
      });
      alert("Rounds saved successfully");
    } catch (error) {
      console.error("Error saving rounds:", error);
      alert("Error saving rounds");
    }
  };

  const handleCreateRoundAndHeat = () => {
    const newRound = {
      id: `round-${rounds.length + 1}`,
      name: `Round 1`,
      category: "",
      sub_category: "",
      board_type: "",
      gender: "",
      age_category: "",
      heats: [
        {
          id: `heat-${1}`,
          name: "Heat 1",
          competitors: Array(4).fill({ name: "", category: "", sub_category: "", board_type: "", gender: "", age_category: "" }),
        },
      ],
    };

    setRounds([...rounds, newRound]);
  };

  const addCompetitorToHeat = (competitor) => {
    const updatedRounds = rounds.map((round) => {
      if (round.id === "round-1") {
        const heat = round.heats[0];
        const emptySlotIndex = heat.competitors.findIndex(c => c.name === "");

        if (emptySlotIndex !== -1) {
          heat.competitors[emptySlotIndex] = competitor;
        } else {
          alert("Heat 1 is already full.");
        }
      }
      return round;
    });

    setRounds(updatedRounds);
  };

  return (
    <Container className="mt-5">
      <h2>Manage Events</h2>
      <form onSubmit={handleCreateEvent} className="mb-4">
        <div className="form-group">
          <label>Event Name:</label>
          <input
            type="text"
            className="form-control"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div className="form-group mt-3">
          <label>Date:</label>
          <input
            type="date"
            className="form-control"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
        </div>
        <div className="form-group mt-3">
          <label>Location:</label>
          <input
            type="text"
            className="form-control"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary mt-3">
          Create Event
        </button>
      </form>

      <EventsList
        events={events}
        fetchCompetitors={fetchCompetitors}
        fetchJudges={fetchJudges}
        handleDeleteEvent={handleDeleteEvent}
      />

      {eventId ? (
        <>
          <h3>Manage Competitors for {selectedEventName}</h3>
          <AddCompetitorForm addCompetitorToHeat={addCompetitorToHeat} />

          <h3>Manage Judges for {selectedEventName}</h3>
          <AddJudgeForm eventId={eventId} fetchJudges={fetchJudges} />

          <JudgesList
            judges={judges}
            selectedEventName={selectedEventName}
            handleDeleteJudge={handleDeleteJudge}
          />

          <h3>Rounds</h3>
          <Button onClick={handleCreateRoundAndHeat} className="mb-4">
            Create Round and Heat
          </Button>
          <Button onClick={handleSaveRounds} className="mb-4">
            Save Rounds
          </Button>

          {rounds.map((round) => (
            <div key={round.id}>
              <h4>{round.name}</h4>
              <Row>
                {round.heats.map((heat) => (
                  <Col key={heat.id} md={6}>
                    <Card className="mb-4">
                      <Card.Body>
                        <Card.Title>{heat.name}</Card.Title>
                        <ul>
                          {heat.competitors.map((competitor, idx) => (
                            <li key={idx}>{competitor.name}</li>
                          ))}
                        </ul>
                      </Card.Body>
                    </Card>
                  </Col>
                ))}
              </Row>
            </div>
          ))}
        </>
      ) : (
        <p className="mt-4">
          Please select an event to manage competitors and judges.
        </p>
      )}
    </Container>
  );
};

export default EventManagement;
