import React, { useState, useEffect } from "react";
import axios from "axios";
import { Container, Row, Col, Card } from "react-bootstrap";

const RoundsDisplay = ({ eventId }) => {
  const [rounds, setRounds] = useState([]);

  const fetchRounds = async () => {
    try {
      const response = await axios.get(
        `http://localhost:3000/event-admin/get-rounds/${eventId}`
      );
      const roundsData = response.data;
      console.log("Rounds data:", roundsData);
      
      const processedRounds = roundsData.map((round) => ({
        id: round.id,
        name: round.round_name,
        category: round.category,
        sub_category: round.sub_category,
        board_type: round.board_type,
        gender: round.gender,
        age_category: round.age_category,
        heats: (round.heats || []).map((heat) => ({
          id: heat.id,
          name: heat.heat_name,
          competitors: (heat.competitors || []).map((competitor) => ({
            id: competitor.id,
            name: competitor.name,
          })),
        })),
      }));

      console.log("Processed rounds:", processedRounds);

      // Filter only rounds with heats
      const roundsWithHeats = processedRounds.filter(round => round.heats.length > 0);

      setRounds(roundsWithHeats);
    } catch (error) {
      console.error("Error fetching rounds:", error);
    }
  };

  useEffect(() => {
    if (eventId) {
      fetchRounds();
    }
  }, [eventId]);

  return (
    <Container className="my-5">
      <h3>Rounds</h3>
      {rounds.length === 0 ? (
        <p>No rounds with heats available.</p>
      ) : (
        rounds.map((round) => (
          <div key={round.id} className="mb-5">
            <h4>{round.name}</h4>
            <Row>
              {round.heats.map((heat) => (
                <Col key={heat.id} sm={12} md={6} lg={4} className="mb-4">
                  <Card>
                    <Card.Body>
                      <Card.Title>{heat.name}</Card.Title>
                      <Card.Text>
                        <ul className="list-group">
                          {heat.competitors.map((competitor) => (
                            <li key={competitor.id} className="list-group-item">
                              {competitor.name}
                            </li>
                          ))}
                        </ul>
                      </Card.Text>
                    </Card.Body>
                  </Card>
                </Col>
              ))}
            </Row>
          </div>
        ))
      )}
    </Container>
  );
};

export default RoundsDisplay;
