import React, { useState, useEffect } from "react";
import axios from "axios";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import "bootstrap/dist/css/bootstrap.min.css";
import { jsxDEV as _jsxDEV, Fragment as _Fragment } from "react/jsx-dev-runtime";
const categories = ["Shortboard", "Longboard"];
const subCategories = ["Men", "Women"];
const ageCategories = ["Open", "Sub12", "Sub14", "Sub18", "Professional", "+40", "+45", "+50", "+60", "+65", "+70"];
const EventManagement = () => {
  _s();
  const [events, setEvents] = useState([]);
  const [competitors, setCompetitors] = useState([]);
  const [judges, setJudges] = useState([]);
  const [rounds, setRounds] = useState([]);
  const [showRounds, setShowRounds] = useState(false);
  const [eventId, setEventId] = useState(null);
  const [name, setName] = useState("");
  const [date, setDate] = useState("");
  const [location, setLocation] = useState("");
  const [competitorName, setCompetitorName] = useState("");
  const [category, setCategory] = useState(categories[0]);
  const [subCategory, setSubCategory] = useState(subCategories[0]);
  const [boardType, setBoardType] = useState(categories[0]);
  const [gender, setGender] = useState(subCategories[0]);
  const [ageCategory, setAgeCategory] = useState(ageCategories[0]);
  const [judgeName, setJudgeName] = useState("");
  const [createdBy, setCreatedBy] = useState(1);
  const [selectedEventName, setSelectedEventName] = useState("");
  useEffect(() => {
    fetchEvents();
  }, []);
  const fetchEvents = async () => {
    try {
      const response = await axios.get("http://localhost:3000/event-admin/get-events");
      setEvents(response.data);
    } catch (error) {
      console.error("Error fetching events:", error);
    }
  };
  const fetchCompetitors = async (eventId, eventName) => {
    try {
      const response = await axios.get(`http://localhost:3000/event-admin/get-competitors/${eventId}`);
      setCompetitors(response.data);
      setEventId(eventId);
      setSelectedEventName(eventName);
    } catch (error) {
      console.error("Error fetching competitors:", error);
    }
  };
  const fetchJudges = async eventId => {
    try {
      const response = await axios.get(`http://localhost:3000/event-admin/get-judges/${eventId}`);
      setJudges(response.data);
    } catch (error) {
      console.error("Error fetching judges:", error);
    }
  };
  const handleCreateEvent = async e => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:3000/event-admin/create-event", {
        name,
        date,
        location,
        created_by: createdBy
      });
      alert("Event created successfully");
      setName("");
      setDate("");
      setLocation("");
      fetchEvents();
    } catch (error) {
      console.error("Error creating event:", error);
      alert("Error creating event");
    }
  };
  const handleDeleteEvent = async id => {
    try {
      await axios.delete(`http://localhost:3000/event-admin/delete-event/${id}`);
      alert("Event deleted successfully");
      fetchEvents();
    } catch (error) {
      console.error("Error deleting event:", error);
      alert("Error deleting event");
    }
  };
  const handleAddCompetitor = async e => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:3000/event-admin/add-competitor", {
        name: competitorName,
        event_id: eventId,
        category,
        sub_category: subCategory,
        board_type: boardType,
        gender,
        age_category: ageCategory
      });
      alert("Competitor added successfully");
      setCompetitorName("");
      setCategory(categories[0]);
      setSubCategory(subCategories[0]);
      setBoardType(categories[0]);
      setGender(subCategories[0]);
      setAgeCategory(ageCategories[0]);
      fetchCompetitors(eventId, selectedEventName);
    } catch (error) {
      console.error("Error adding competitor:", error);
      alert("Error adding competitor");
    }
  };
  const handleDeleteCompetitor = async id => {
    try {
      await axios.delete(`http://localhost:3000/event-admin/delete-competitor/${id}`);
      alert("Competitor deleted successfully");
      fetchCompetitors(eventId, selectedEventName);
    } catch (error) {
      console.error("Error deleting competitor:", error);
      alert("Error deleting competitor");
    }
  };
  const handleAddJudge = async e => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:3000/event-admin/add-judge", {
        name: judgeName,
        event_id: eventId
      });
      alert("Judge added successfully");
      setJudgeName("");
      fetchJudges(eventId);
    } catch (error) {
      console.error("Error adding judge:", error);
      alert("Error adding judge");
    }
  };
  const handleDeleteJudge = async id => {
    try {
      await axios.delete(`http://localhost:3000/event-admin/delete-judge/${id}`);
      alert("Judge deleted successfully");
      fetchJudges(eventId);
    } catch (error) {
      console.error("Error deleting judge:", error);
      alert("Error deleting judge");
    }
  };
  const handleSaveRounds = async () => {
    try {
      await axios.post("http://localhost:3000/event-admin/save-rounds", {
        eventId,
        rounds: rounds.map(round => ({
          name: round.name,
          category: round.category,
          sub_category: round.sub_category,
          board_type: round.board_type,
          gender: round.gender,
          age_category: round.age_category,
          heats: round.heats.map(heat => ({
            heat_name: heat.name,
            competitors: heat.competitors.map(competitor => ({
              id: competitor.id,
              name: competitor.name
            }))
          }))
        }))
      });
      alert("Rounds saved successfully");
    } catch (error) {
      console.error("Error saving rounds:", error);
      alert("Error saving rounds");
    }
  };
  const handleFetchRounds = async () => {
    try {
      const response = await axios.get(`http://localhost:3000/event-admin/get-rounds/${eventId}`);
      const roundsData = response.data;
      console.log("Initial rounds data:", roundsData);
      if (!roundsData || !Array.isArray(roundsData)) {
        throw new Error("Invalid rounds data");
      }
      const processedRounds = roundsData.map(round => {
        console.log("Processing round:", round);
        if (!round.heats || !Array.isArray(round.heats)) {
          console.warn("No heats found for round:", round);
          return {
            id: round.id,
            name: round.round_name,
            category: round.category,
            sub_category: round.sub_category,
            board_type: round.board_type,
            gender: round.gender,
            age_category: round.age_category,
            heats: []
          };
        }
        const processedHeats = round.heats.map(heat => {
          console.log("Processing heat:", heat);
          const processedCompetitors = heat.competitors ? heat.competitors.map(competitor => ({
            id: competitor.id,
            name: competitor.name
          })) : [];
          console.log("Processed competitors:", processedCompetitors);
          return {
            id: heat.id,
            name: heat.heat_name,
            competitors: processedCompetitors
          };
        });
        console.log("Processed heats:", processedHeats);
        return {
          id: round.id,
          name: round.round_name,
          category: round.category,
          sub_category: round.sub_category,
          board_type: round.board_type,
          gender: round.gender,
          age_category: round.age_category,
          heats: processedHeats
        };
      });
      console.log("Processed rounds:", processedRounds);
      setRounds(processedRounds);
      setShowRounds(true);
    } catch (error) {
      console.error("Error fetching rounds:", error);
      alert("Error fetching rounds");
    }
  };

